package com.programs;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Declared the variable;
		
		byte bval;
		short sval;
		int intval;
		long lval;
        float fval;
        double dval;
        char cval;
        boolean boolval;
        
        // assign value to the variable 
        
        bval=2;
        sval=10;
        intval=100;
        lval=200l;
        fval=10.10f;
        dval=200.10d;
        cval='P';
        boolval=true;
        
        System.out.println("Byte Value = "+bval);
        System.out.println("Short Value = "+sval);
        System.out.println("Integer Value = "+intval);
        System.out.println("Long Value = "+lval);
        System.out.println("Float Value = "+fval);
        System.out.println("Double Value = "+dval);
        System.out.println("Char Value = "+cval);
        System.out.println("Boolean Value = "+boolval);
       
    
	}

}
